formula.derivative:
dxan = (daa + ((dxa * (cos(Xxr) * cosh(Xxi))) + (dya * (sin(Xxr) * sinh(Xxi)))));
dxbn = (dab + ((dxb * (cos(Xxr) * cosh(Xxi))) + (dyb * (sin(Xxr) * sinh(Xxi)))));
dyan = (dba + (((-1) * (dxa * (sin(Xxr) * sinh(Xxi)))) + (dya * (cos(Xxr) * cosh(Xxi)))));
dybn = (dbb + (((-1) * (dxb * (sin(Xxr) * sinh(Xxi)))) + (dyb * (cos(Xxr) * cosh(Xxi)))));
